import{_ as p,c as e,o as t,a}from"./app.485d291c.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u7834\u58C1\u673A","slug":"\u7834\u58C1\u673A","link":"#\u7834\u58C1\u673A","children":[]}],"relativePath":"\u5403/index.md"}'),_={name:"\u5403/index.md"},n=a('<p>\u975E\u8F6C\u57FA\u56E0</p><p>\u517B\u80C3 \u767D\u841D\u535C\uFF0C\u5357\u74DC</p><h2 id="\u7834\u58C1\u673A" tabindex="-1">\u7834\u58C1\u673A <a class="header-anchor" href="#\u7834\u58C1\u673A" aria-hidden="true">#</a></h2><p>\u7C73\u7CCA \u6C34\u6BD4\u4F8B 1\uFF1A4 \u8C46\u6D46 \u6C34\u6BD4\u4F8B 1\uFF1A7</p><p>\u9178\u5976\u6C34\u679C\u9762\u5305\u9E21\u86CB</p><p>\u6613\u4E8E\u6D88\u5316\u7684\u98DF\u7269\uFF0C\u5982\u725B\u5976\u3001\u8C46\u6D46\u3001\u8C46\u8150\u3001\u86CB\u7FB9\u3001\u5C71\u836F\u3001\u80E1\u841D\u535C\u7B49\u3002\u8FDB\u98DF\u65F6\uFF0C\u8981\u7EC6\u56BC\u6162\u54BD\uFF0C\u591A\u98DF\u7CA5\u7C7B\u7B49\u8F6F\u98DF</p><p>\u80C3\u708E\u60A3\u8005\u559D\u5927\u9EA6\u8336\u3001\u8309\u8389\u82B1\u8336\u3001\u4E4C\u6885\u8336\u3001\u666E\u6D31\u8336\u3001\u9648\u76AE\u7518\u8349\u8336\u3001\u7EA2\u8336\u7B49\u53EF\u4EE5\u8D77\u5230\u517B\u80C3\u7684\u4F5C\u7528\u3002</p><p>1\u3001\u5927\u9EA6\u8336\uFF1A\u5927\u9EA6\u8336\u4E3B\u8981\u5177\u6709\u52A9\u6D88\u5316\u3001\u53BB\u6CB9\u817B\u3001\u6E05\u70ED\u89E3\u6691\u3001\u5065\u813E\u7684\u4F5C\u7528\uFF0C\u80C3\u708E\u60A3\u8005\u7ECF\u5E38\u559D\u5927\u9EA6\u8336\uFF0C\u80FD\u5E2E\u52A9\u80A0\u80C3\u9053\u6D88\u5316\uFF0C\u4E0D\u4EC5\u53EF\u4EE5\u964D\u80C3\u706B\uFF0C\u8FD8\u5177\u6709\u517B\u80C3\u7684\u6548\u679C\u3002</p><p>2\u3001\u8309\u8389\u82B1\u8336\uFF1A\u8309\u8389\u82B1\u8336\u5177\u6709\u6297\u83CC\u6D88\u708E\u3001\u6392\u6BD2\u517B\u989C\u3001\u884C\u6C14\u5F00\u90C1\u7684\u4F5C\u7528\uFF0C\u800C\u4E14\u8309\u8389\u82B1\u8336\u7684\u5473\u9053\u9999\u751C\uFF0C\u5177\u6709\u6DE1\u96C5\u7684\u9999\u5473\uFF0C\u80C3\u708E\u60A3\u8005\u7ECF\u5E38\u559D\u8309\u8389\u82B1\u8336\uFF0C\u6709\u52A9\u4E8E\u6D88\u9664\u80C3\u90E8\u7684\u708E\u75C7\uFF0C\u4ECE\u800C\u8D77\u5230\u517B\u80C3\u6548\u679C\u3002</p><p>3\u3001\u4E4C\u6885\u8336\uFF1A\u4E4C\u6885\u8336\u5177\u6709\u52A9\u6D88\u5316\u3001\u71E5\u6E7F\u5065\u813E\u7684\u529F\u6548\uFF0C\u540C\u65F6\u8FD8\u80FD\u8D77\u5230\u884C\u6C14\u548C\u80C3\u4F5C\u7528\uFF0C\u80C3\u708E\u60A3\u8005\u7ECF\u5E38\u559D\u4E4C\u6885\u8336\u6709\u517B\u80C3\u7684\u6548\u679C\uFF0C\u56E0\u4E3A\u80C3\u708E\u901A\u5E38\u4F1A\u5F15\u8D77\u98DF\u6B32\u4E0B\u964D\uFF0C\u9002\u5F53\u559D\u4E4C\u6885\u8336\u80FD\u8D77\u5230\u5F00\u80C3\u6D88\u98DF\u7684\u4F5C\u7528\u3002</p><p>4\u3001\u666E\u6D31\u8336\uFF1A\u666E\u6D31\u8336\u6E29\u548C\uFF0C\u4E0D\u4F24\u80C3\uFF0C\u800C\u666E\u6D31\u8336\u5F53\u4E2D\u542B\u6709\u5927\u91CF\u591A\u915A\u4EE5\u53CA\u8425\u517B\u7269\u8D28\uFF0C\u6240\u4EE5\u80C3\u708E\u60A3\u8005\u7ECF\u5E38\u559D\u666E\u6D31\u8336\u80FD\u8D77\u5230\u517B\u80C3\u4F5C\u7528\uFF0C\u666E\u6D31\u8336\u5F53\u4E2D\u8FD8\u542B\u6709\u8BB8\u591A\u7684\u751F\u7406\u6D3B\u6027\u6210\u5206\uFF0C\u5177\u6709\u6740\u6BD2\u3001\u6D88\u83CC\u7684\u4F5C\u7528\u3002</p><p>5\u3001\u9648\u76AE\u7518\u8349\u8336\uFF1A\u80C3\u708E\u60A3\u8005\u7ECF\u5E38\u559D\u9648\u76AE\u7518\u8349\u8336\u4E5F\u5177\u6709\u517B\u80C3\u6548\u679C\uFF0C\u56E0\u9648\u76AE\u7518\u8349\u8336\u6709\u884C\u6C14\u5BBD\u4E2D\u3001\u71E5\u6E7F\u5316\u75F0\u3001\u52A9\u6D88\u5316\u7684\u4F5C\u7528\u3002</p><p>6\u3001\u7EA2\u8336\uFF1A\u7EA2\u8336\u5177\u6709\u89E3\u6BD2\u3001\u6740\u83CC\u3001\u5229\u5C3F\u3001\u6E05\u70ED\u3001\u751F\u6D25\u3001\u63D0\u795E\u9192\u8111\u7684\u529F\u6548\uFF0C\u800C\u4E14\u7EA2\u8336\u5F53\u4E2D\u8FD8\u542B\u6709\u8F83\u591A\u7684\u591A\u915A\u7269\u8D28\uFF0C\u80C3\u708E\u60A3\u8005\u7ECF\u5E38\u559D\u7EA2\u8336\uFF0C\u80FD\u5E2E\u52A9\u4EBA\u4F53\u6D88\u5316\uFF0C\u540C\u65F6\u8FD8\u80FD\u517B\u80C3\u3002</p><p>\u80C3\u708E\u60A3\u8005\u9664\u4E86\u559D\u8336\u517B\u80C3\u4E4B\u5916\uFF0C\u8FD8\u8981\u79EF\u6781\u8FDB\u884C\u6CBB\u7597\uFF0C\u53EF\u4EE5\u670D\u7528\u6291\u5236\u80C3\u9178\u7C7B\u7684\u836F\u7269\uFF0C\u6BD4\u5982\u6CD5\u83AB\u66FF\u4E01\u3001\u96F7\u5C3C\u66FF\u4E01\u3002\u8FD8\u8981\u7ED9\u4E88\u5E7D\u95E8\u87BA\u65CB\u6746\u83CC\u6CBB\u7597\uFF0C\u4F8B\u5982\u7532\u785D\u5511\u3001\u963F\u83AB\u897F\u6797\u7B49\u3002\u540C\u65F6\u4E5F\u8981\u7ED9\u4E88\u4FC3\u8FDB\u80C3\u52A8\u529B\u7684\u836F\u7269\uFF0C\u6BD4\u5982\u83AB\u6C99\u5FC5\u5229\u3001\u76D0\u9178\u4F0A\u6258\u5FC5\u5229\u7247\u7B49\u3002</p><p>\u51AC\u5B63\u559D\u4EE5\u4E0B\u51E0\u79CD\u8336\u6BD4\u8F83\u597D\uFF1A</p><p>1\u3001\u73AB\u7470\u82B1\u8336\uFF1A\u5982\u679C\u662F\u5973\u6027\u60A3\u8005\uFF0C\u5E73\u65F6\u6709\u6708\u7ECF\u4E0D\u8C03\u6216\u8005\u6709\u75DB\u7ECF\u7684\u95EE\u9898\uFF0C\u51AC\u5B63\u53EF\u4EE5\u559D\u73AB\u7470\u82B1\u8336\uFF0C\u53EF\u4EE5\u8D77\u5230\u8C03\u7406\u6708\u7ECF\u3001\u6D3B\u8840\u5316\u7600\u7684\u529F\u6548\uFF0C\u53EF\u4EE5\u6539\u5584\u60A3\u8005\u75DB\u7ECF\u7684\u95EE\u9898\uFF1B</p><p>2\u3001\u8309\u8389\u82B1\u8336\uFF1A\u5BAB\u5BD2\u7684\u5973\u6027\uFF0C\u5728\u6708\u7ECF\u671F\u95F4\u65F6\u53EF\u4EE5\u670D\u7528\u8309\u8389\u82B1\u8336\uFF0C\u4EE5\u8FBE\u5230\u6CBB\u7597\u809D\u6C14\u90C1\u7ED3\u5F15\u8D77\u6708\u7ECF\u4E0D\u8C03\u7684\u4F5C\u7528\uFF1B</p><p>3\u3001\u7EA2\u8336\u6216\u59DC\u67A3\u8336\uFF1A\u666E\u901A\u4EBA\u5982\u679C\u662F\u5BD2\u6027\u7684\u4F53\u8D28\uFF0C\u5728\u51AC\u5929\u56E0\u4E3A\u5929\u6C14\u5BD2\u51B7\uFF0C\u4E5F\u53EF\u4EE5\u559D\u7EA2\u8336\u6216\u8005\u59DC\u67A3\u8336\uFF0C\u53EF\u4EE5\u8D77\u5230\u6E29\u7ECF\u6563\u5BD2\u3001\u6D3B\u8840\u901A\u7EDC\u3001\u751F\u70ED\u6696\u8179\u7684\u529F\u6548\uFF0C\u53EF\u4EE5\u5F3A\u8EAB\u5065\u4F53\uFF1B</p><p>4\u3001\u666E\u6D31\u8336\uFF1A\u5E73\u65F6\u559D\u666E\u6D31\u8336\uFF0C\u8D77\u5230\u517B\u80C3\u3001\u6E05\u70ED\u6696\u80C3\u7684\u4F5C\u7528\uFF1B</p><p>5\u3001\u4E4C\u9F99\u8336\uFF1A\u4E4C\u9F99\u8336\u53EF\u4EE5\u9632\u6B62\u53E3\u5E72\u820C\u71E5\u7684\u60C5\u51B5\uFF0C\u8FD8\u80FD\u591F\u8D77\u5230\u6DA6\u80A4\u3001\u6DA6\u5589\u7684\u4F5C\u7528\uFF0C\u56E0\u4E3A\u5728\u51AC\u5929\uFF0C\u5C24\u5176\u662F\u5317\u65B9\uFF0C\u5929\u6C14\u6BD4\u8F83\u5E72\u71E5\u3001\u5BD2\u51B7\uFF0C\u559D\u4E4C\u9F99\u8336\u6548\u679C\u6BD4\u8F83\u597D\uFF1B</p><p>6\u3001\u82B1\u8336\uFF1A\u5982\u679C\u662F\u5929\u6C14\u6BD4\u8F83\u5E72\u71E5\u7684\u60C5\u51B5\uFF0C\u4E5F\u53EF\u4EE5\u559D\u82B1\u8336\u6765\u8FBE\u5230\u758F\u809D\u89E3\u90C1\u7684\u4F5C\u7528\uFF0C\u53EF\u4EE5\u7F13\u89E3\u60C5\u7EEA\u3001\u517B\u809D\u5229\u80C6\u3001\u6E05\u809D\u660E\u76EE\u3001\u63D0\u795E\u9192\u8111\uFF1B</p><p>7\u3001\u9ED1\u8336\uFF1A\u5E73\u65F6\u53EF\u4EE5\u559D\u9ED1\u8336\uFF0C\u4E5F\u80FD\u591F\u8D77\u5230\u5FA1\u5BD2\u3001\u964D\u8840\u7CD6\u3001\u964D\u8840\u8102\u7684\u529F\u6548\u3002</p><p>\u65E0\u8BBA\u5728\u51AC\u5B63\u559D\u4EC0\u4E48\u8336\uFF0C\u60A3\u8005\u90FD\u8981\u6CE8\u610F\u5BF9\u81EA\u8EAB\u7684\u8C03\u517B\uFF0C\u5916\u51FA\u65F6\u8981\u505A\u597D\u4FDD\u6696\u7684\u5DE5\u4F5C\uFF0C\u5207\u4E0D\u53EF\u8D2A\u98DF\u51B7\u996E\u7B49\u3002</p><p>\u590F\u5929\u9002\u5408\u559D\u7684\u8336\u6BD4\u8F83\u591A\uFF0C\u5E38\u89C1\u7684\u6709\u7EFF\u8336\u3001\u59DC\u8336\u3001\u767D\u8336\u3001\u9ED1\u8336\u3001\u83CA\u82B1\u8336\u7B49\uFF0C\u53EF\u4EE5\u6839\u636E\u4E2A\u4EBA\u4F53\u8D28\u6216\u8336\u53F6\u7684\u6027\u8D28\u6765\u51B3\u5B9A\u3002</p><p>1\u3001\u7EFF\u8336\uFF1A\u590F\u5929\u6BD4\u8F83\u708E\u70ED\uFF0C\u5F88\u5BB9\u6613\u5F15\u8D77\u4E0A\u706B\uFF0C\u800C\u7EFF\u8336\u5C5E\u6027\u5BD2\u51C9\uFF0C\u5177\u6709\u4E00\u5B9A\u7684\u6D88\u70ED\u6D88\u6691\u3001\u53BB\u6BD2\u53BB\u706B\u3001\u6B62\u6E34\u751F\u6D25\u4F5C\u7528\uFF0C\u5E76\u4E14\u53E3\u611F\u6E05\u65B0\u723D\u53E3\uFF0C\u9002\u5F53\u996E\u7528\u53EF\u4EE5\u5E2E\u52A9\u7F13\u89E3\u590F\u5929\u5F15\u8D77\u7684\u6691\u70ED\u3002\u53E6\u5916\uFF0C\u7EFF\u8336\u4E2D\u542B\u6709\u5496\u5561\u78B1\uFF0C\u8BE5\u6210\u5206\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u53EF\u4EE5\u523A\u6FC0\u4E2D\u67A2\u795E\u7ECF\uFF0C\u5E2E\u52A9\u5524\u9192\u5927\u8111\uFF0C\u6709\u52A9\u4E8E\u63D0\u795E\uFF1B</p><p>2\u3001\u59DC\u8336\uFF1A\u867D\u7136\u590F\u5929\u708E\u70ED\uFF0C\u4F46\u5B9E\u9645\u4E0A\u673A\u4F53\u5BB9\u6613\u5916\u70ED\u5185\u5BD2\uFF0C\u7279\u522B\u662F\u7ECF\u5E38\u5F85\u5728\u7A7A\u8C03\u623F\u5185\u6216\u559D\u51B7\u996E\u6765\u6D88\u6691\u7684\u4EBA\u7FA4\uFF0C\u957F\u6B64\u4EE5\u5F80\u53EF\u80FD\u4F1A\u635F\u4F24\u813E\u80C3\uFF0C\u8FDB\u800C\u8BF1\u53D1\u8179\u75DB\u3001\u8179\u6CFB\u7B49\u4E0D\u9002\u3002\u751F\u59DC\u6027\u6E29\uFF0C\u6B64\u65F6\u559D\u4E00\u676F\u6E29\u70ED\u7684\u59DC\u8336\u662F\u4E00\u4E2A\u4E0D\u9519\u7684\u9009\u62E9\uFF0C\u53EF\u4EE5\u5E2E\u52A9\u673A\u4F53\u8C03\u548C\uFF0C\u5728\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u80FD\u591F\u795B\u9664\u4F53\u5185\u7684\u5BD2\u6C14\u3002\u540C\u65F6\u4E5F\u80FD\u5E2E\u52A9\u5065\u813E\u6696\u80C3\uFF0C\u7279\u522B\u9002\u5408\u813E\u80C3\u865A\u5BD2\u8005\u996E\u7528\uFF1B</p><p>3\u3001\u767D\u8336\uFF1A\u767D\u8336\u9C9C\u9187\u53EF\u53E3\u4E14\u6027\u6E05\u51C9\uFF0C\u8F83\u4E3A\u9002\u5B9C\u590F\u5B63\u996E\u98DF\u8FC7\u4E8E\u6CB9\u817B\u8005\u996E\u7528\uFF0C\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u53EF\u4EE5\u89E3\u817B\u3002\u53E6\u5916\u809D\u706B\u65FA\u76DB\u8005\u4E5F\u53EF\u4EE5\u9002\u91CF\u996E\u7528\u6B64\u8336\uFF0C\u80FD\u591F\u5E2E\u52A9\u6E05\u70ED\u6DA6\u80BA\uFF0C\u540C\u65F6\u8FD8\u53EF\u4EE5\u5E2E\u52A9\u9884\u9632\u4E2D\u6691\uFF1B</p><p>4\u3001\u9ED1\u8336\uFF1A\u5C5E\u4E8E\u53D1\u9175\u8336\uFF0C\u6309\u7167\u5730\u57DF\u5206\u7C7B\u6709\u4E0D\u540C\u7684\u54C1\u79CD\uFF0C\u5982\u4E91\u5357\u666E\u6D31\u8336\u7B49\uFF0C\u6027\u5E73\u4E14\u6709\u4E00\u5B9A\u7684\u6D88\u6691\u3001\u89E3\u6BD2\u4F5C\u7528\uFF0C\u540C\u65F6\u4E5F\u80FD\u591F\u5E2E\u52A9\u62A4\u80C3\u3001\u901A\u4FBF\u3002\u590F\u5B63\u591A\u6570\u4EBA\u996E\u98DF\u8F83\u6742\uFF0C\u5C24\u5176\u559C\u6B22\u901A\u8FC7\u5404\u79CD\u51B7\u996E\u53BB\u70ED\uFF0C\u5F88\u5BB9\u6613\u8BF1\u53D1\u6025\u6027\u80C3\u80A0\u708E\uFF0C\u4E5F\u5BB9\u6613\u51FA\u73B0\u6D88\u5316\u4E0D\u826F\u548C\u4FBF\u79D8\u7684\u60C5\u51B5\u3002\u6B64\u65F6\u9002\u91CF\u996E\u7528\u9ED1\u8336\u53EF\u4EE5\u5E2E\u52A9\u8C03\u8282\u80A0\u80C3\uFF0C\u4E5F\u80FD\u5E2E\u52A9\u6539\u5584\u56E0\u4FBF\u79D8\u800C\u5F15\u53D1\u7684\u75D8\u75D8\u548C\u53E3\u81ED\u95EE\u9898\uFF1B</p><p>5\u3001\u83CA\u82B1\u8336\uFF1A\u6B64\u8336\u5473\u7518\u3001\u6027\u5FAE\u5BD2\uFF0C\u5177\u6709\u4E00\u5B9A\u7684\u6563\u98CE\u70ED\u548C\u6E05\u809D\u660E\u76EE\u7684\u4F5C\u7528\uFF0C\u5BF9\u4E8E\u56E0\u4E0A\u706B\u800C\u5F15\u8D77\u957F\u75D8\u7684\u4EBA\u8F83\u4E3A\u9002\u5B9C\u3002\u80FD\u591F\u5E2E\u52A9\u8D77\u5230\u8212\u7F13\u4F5C\u7528\uFF0C\u4F46\u4E0D\u9002\u7528\u4E8E\u813E\u80C3\u865A\u5BD2\u548C\u9633\u865A\u4F53\u8D28\u8005\u996E\u7528\uFF0C\u4EE5\u514D\u635F\u4F24\u6B63\u6C14\u3002</p>',29),r=[n];function s(o,c,i,d,l,h){return t(),e("div",null,r)}const u=p(_,[["render",s]]);export{m as __pageData,u as default};
