import{_ as p,c as t,o as e,a as _}from"./app.485d291c.js";const S=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"\u73A9/\u9A91\u884C/2.md"}'),s={name:"\u73A9/\u9A91\u884C/2.md"},a=_("<p>\u76EE\u524D\u7684\u6700\u9AD8\u7968\u56DE\u7B54\u4E2D\u8D34\u51FA\u7684\u7EBF\u8DEF\uFF0C\u57FA\u672C\u56CA\u62EC\u4E86\u5317\u4EAC\u5468\u8FB9\u9A91\u884C\u7684\u6240\u6709\u8DEF\u7EBF\uFF0C\u60F3\u5FC5\u5728\u5317\u4EAC\u7684\u8BF8\u4F4D\u9A91\u53CB\u4E5F\u5728\u5176\u4ED6\u5730\u65B9\u770B\u5230\u8FC7\u3002</p><p>\u5728\u8FD9\u91CC\u8865\u5145\u4E00\u4E0B\u81EA\u5DF1\u53BB\u8FC7\u7684\u4E94\u6761\u7EBF\u8DEF\u7684\u8BE6\u7EC6\u9A91\u884C\u653B\u7565\uFF0C\u4E5F\u7B97\u662F\u5BF9\u6700\u8FD1\u9A91\u884C\u7684\u603B\u7ED3\u5427\uFF0C\u5206\u522B\u662F\uFF1A\u5999\u5CF0\u5C71\u7EBF\u3001\u767D\u7F8A\u6C9F\u7EBF\u3001\u73AF\u5341\u4E09\u9675\u7EBF\u3001\u5341\u6E21\u7EBF\u3001\u767E\u91CC\u753B\u5ECA\uFF0D\u767D\u6CB3\u5CE1\u8C37\u7EBF\u3002</p><p>\u4E94\u6761\u7EBF\u8DEF\u90FD\u662F\u7ED3\u5408\u5176\u4ED6\u4EBA\u7684\u6E38\u8BB0\u81EA\u5DF1\u7F16\u5199\u7684\uFF0C\u4E0D\u8DB3\u4E4B\u5904\u8BF7\u5404\u4F4D\u8001\u9A74\u8F7B\u62CD\u3002</p><p>\u90E8\u5206\u56FE\u7247\u6765\u81EA\u7F51\u7EDC\uFF0C\u5982\u6709\u4FB5\u72AF\uFF0C\u8BF7\u8054\u7CFB\u6211\uFF0C\u7ACB\u523B\u5220\u9664\u3002</p><p>\u5199\u5B8C\u540E\u53D1\u73B0\uFF0C\u5FEB\u8981\u4E00\u4E07\u5B57\u4E86\u90FD\uFF01\u6C42\u8D5E\u540C\u9F13\u52B1\uFF01<em>(^o^)/</em></p><p>\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D</p><p>\u4E00.\u5999\u5CF0\u5C71\u7EBF\uFF1A \u9A91\u884C\u533A\u57DF\u4ECB\u7ECD\uFF1A</p><p>\u5999\u5CF0\u5C71\u662F\u5317\u4EAC\u5E02\u540D\u5C71\u4E4B\u4E00\uFF0C\u4EAC\u90CA\u6E38\u89C8\u80DC\u5730\uFF0C\u4F4D\u4E8E\u4EAC\u897F\u95E8\u5934\u6C9F\u533A\u5883\u5185\uFF0C\u95E8\u5934\u6C9F\u533A\u4E0E\u660C\u5E73\u53BF\u754C\u5C71\uFF0C\u5C5E\u592A\u884C\u5C71\u4F59\u8109\u3002\u4F4D\u4E8E\u5317\u4EAC\u897F\u531730\u4F59\u516C\u91CC\uFF0C\u8DDD\u5E02\u533A55\u516C\u91CC\uFF0C\u5999\u5CF0\u5C71\u5C5E\u592A\u884C\u5C71\u8109\uFF0C\u706B\u6210\u5CA9\u7ED3\u6784\uFF0C\u4E3B\u5CF0\u6D77\u62D41291\u7C73\uFF0C\u5C71\u52BF\u5CED\u62D4\uFF0C\u82B1\u8349\u6E05\u4E3D\u3002\u5999\u5CF0\u5C71\u6709\u65E5\u51FA\u3001\u665A\u971E\u3001\u96FE\u6DDE\u3001\u5C71\u5E02\u7B49\u65F6\u4EE4\u666F\u89C2\u3001\u6709\u6234\u201C\u534E\u5317\u4E00\u7EDD\u201D\u6842\u51A0\u7684\u5343\u4EA9\u73AB\u7470\u82B1\u3001\u6709\u534E\u5317\u5730\u533A\u89C4\u6A21\u6700\u5927\u7684\u4F20\u7EDF\u671D\u5723\u5E99\u4F1A\u3001\u662F\u5317\u4EAC\u5468\u8FB9\u6700\u5177\u6587\u5316\u5E95\u8574\u7684\u98CE\u666F\u540D\u80DC\u533A\u4E4B\u4E00\u3002\u65E7\u540D\u4EF0\u5C71\uFF0C\u4EE5\u201C\u53E4\u5E99\u201D\u3001\u201C\u5947\u677E\u201D\u3001\u201C\u602A\u77F3\u201D\u3001\u201C\u5F02\u5349\u201D\u800C\u95FB\u540D\u3002\u56E0\u5C71\u52BF\u96C4\u5CFB\uFF0C\u4E94\u5CF0\u5E76\u4E3E\uFF0C\u5999\u9AD8\u4E3A\u5176\u4E00\uFF0C\u6545\u4EA6\u79F0\u5999\u9AD8\u5CF0\u3002</p><p>\u5999\u5CF0\u5C71\u7EBF\u662F\u5317\u4EAC\u8FD1\u90CA\u975E\u5E38\u7ECF\u5178\u7684\u4E00\u6761\u7EBF\u8DEF\uFF0C\u56E0\u4E3A\u517621\u516C\u91CC\u7684\u722C\u5761\u8DEF\u7EBF\uFF0C\u5BF9\u4E8E\u5F88\u591A\u5728\u5317\u4EAC\u7684\u81EA\u884C\u8F66\u9A91\u884C\u7231\u597D\u8005\u6765\u8BF4\uFF0C\u5999\u5CF0\u5C71\u90FD\u8C61\u5F81\u7740\u4E00\u4E2A\u574E\uFF0C\u8FC8\u8FC7\u4E86\u8FD9\u4E2A\u574E\uFF0C\u610F\u5473\u7740\u9A91\u884C\u7684\u5F3A\u5EA6\u5C31\u4E0A\u4E86\u4E00\u4E2A\u65B0\u7684\u53F0\u9636\u3002</p><p>\u9A91\u884C\u7EA7\u522B\uFF1A\u2605\u2605</p><p>\u4F53\u529B\u5F3A\u5EA6\uFF1A\u2605\u2605\u2605</p><p>\u6280\u672F\u96BE\u5EA6\uFF1A\u2605\u2605\u2605</p><p>\u60CA\u9669\u523A\u6FC0\uFF1A\u2605\u2605</p><p>\u81EA\u7136\u98CE\u5149\uFF1A\u2605\u2605\u2605</p><p>\u5386\u53F2\u6587\u5316\uFF1A\u2605\u2605\u2605</p><p>\u9A91\u884C\u8DEF\u7EBF\uFF1A</p><p>\u822A\u5929\u6865\uFF0D\u6A21\u5F0F\u53E3\uFF0D\u519B\u5E84\uFF0D\u5999\u5CF0\u5C71\uFF0D\u519B\u5E84\uFF0D\u6A21\u5F0F\u53E3\uFF0D\u822A\u5929\u6865\uFF08\u539F\u8DEF\u8FD4\u7A0B\uFF09</p><p>\u9A91\u884C\u5B63\u8282\uFF1A</p><p>3\u6708\u201411\u6708\u3002\u6625\u5B634\u20146\u6708\u4EFD\u5999\u5CF0\u5C71\u4F1A\u4E3E\u529E\u5E99\u4F1A\u6D3B\u52A8\uFF0C\u4E145\u6708\u5E95\u6B63\u662F\u73AB\u7470\u8C37\u7684\u4E07\u4EA9\u73AB\u7470\u5F00\u653E\u7684\u65F6\u8282\uFF0C\u56E0\u6B64\u5F3A\u70C8\u63A8\u83504\u20146\u6708\u4EFD\u53BB\u9A91\u884C\u3002</p><p>\u5168\u7EBF\u65F6\u95F4\uFF1A</p><p>\u5355\u65E5\u8FD4\u7A0B\uFF0C\u603B\u9A91\u884C\u65F6\u95F4\u7EA68\u5C0F\u65F6\u300221\u516C\u91CC\u7684\u722C\u5761\u5C71\u8DEF\u6839\u636E\u4E2A\u4EBA\u4F53\u529B\u548C\u6280\u672F\u80FD\u529B\u4E0D\u540C\u81EA\u884C\u63A7\u5236\uFF0C\u4E00\u822C\u60C5\u51B5\u4E0B\u5E73\u5747\u6210\u7EE9\u4E3A100\u5206\u949F\u5DE6\u53F3\u3002</p><p>\u8DEF\u7EBF\u8BC4\u4EF7\uFF1A</p><p>\u5C31\u9A91\u884C\u800C\u8A00\uFF0C\u5999\u5CF0\u5C71\u7EBF\u4F18\u52BF\u5728\u4E8E\uFF1A</p><p>\u4E00\u3001\u79BB\u5E02\u533A\u8FD1\uFF0C\u5B83\u662F\u79BB\u5E02\u533A\u6700\u8FD1\u7684\u516C\u8DEF\u578B\u9AD8\u5C71\uFF0C\u4E0A\u5347\u6D77\u62D41000\u7C73\u4EE5\u4E0A\uFF0C21\u516C\u91CC\u76D8\u5C71\u9053\u88AB\u6237\u5916\u63A2\u9669\u6742\u5FD7\u8BC4\u4E3A\u9A91\u884C\u96BE\u5EA6\u4EC5\u6B21\u4E8E\u201C\u56DB\u6D77\u57AD\u53E3\u201D\u7684\u4E09\u661F\u8DEF\u6BB5\uFF0C\u53EF\u4EE5\u5145\u5206\u4EAB\u53D7\u4E0A\u5761\u4E0E\u653E\u5761\u7684\u4E50\u8DA3\u3002</p><p>\u4E8C\u3001\u4EBA\u70DF\u7A00\u5C11\uFF0C\u5999\u5CF0\u5C71\u9876\u662F\u6B7B\u8DEF\u7EC8\u70B9\uFF0C\u9664\u4E86\u6DA7\u6C9F\u6751\u6C11\uFF0C\u5A18\u5A18\u5E99\u5927\u796D\u5C0F\u62DC\u4E0A\u9999\u5916\uFF0C\u5E73\u65F6\u5F88\u5C11\u6709\u4EBA\u524D\u5F80\uFF0C\u5C06\u4E8B\u6545\u7387\u964D\u5230\u4E86\u6700\u4F4E\u3002</p><p>\u5EFA\u8BAE\u4EBA\u6570\uFF1A</p><p>\u8BE5\u9A91\u884C\u8DEF\u7EBF\u76F8\u5BF9\u6210\u719F\uFF0C\u9A91\u884C\u4EBA\u6570\u6CA1\u6709\u592A\u591A\u9650\u5236\uFF0C\u63A8\u83502\u20144\u4EBA\u7684\u5C0F\u578B\u961F\u4F0D\u4F11\u95F2\u9A91\u884C\u3002</p><p>\u6700\u4F73\u666F\u70B9\uFF1A</p><p>\u73AB\u7470\u8C37\u3001\u5A18\u5A18\u5E99\u3001\u201C\u7D2B\u971E\u4E00\u7EBF\u5929\u201D\u5C0F\u666F\u533A\u3001\u5927\u4E91\u5BFA\u9057\u5740\u3001\u5E84\u58EB\u6566\u522B\u5885</p><p>\u5FC5\u9700\u88C5\u5907\uFF1A</p><p>\u53D8\u901F\u8F66\uFF08\u5C71\u5730\u4F18\u5148\u3001\u516C\u8DEF\u8D5B\u5176\u6B21\uFF09\uFF0C\u56E0\u4E3A\u6709\u4E00\u4E2A21\u516C\u91CC\u7684\u957F\u5761\u722C\u884C\uFF0C\u57CE\u5E02\u901A\u52E4\u81EA\u884C\u8F66\u4F1A\u76F8\u5F53\u8D39\u529B\uFF0C\u56E0\u6B64\u4E0D\u5EFA\u8BAE\u91C7\u7528\u3002</p><p>\u9632\u98CE\u4E0A\u8863\u3001\u6392\u6C57\u5185\u8863\u3001\u80CC\u5305\u3001\u58A8\u955C\u3001\u5934\u76D4\u3001\u624B\u5957</p><p>\u8BE5\u7EBF\u8DEF\u662F\u4EAC\u90CA\u7EBF\u8DEF\uFF0C\u6709\u5C06\u8FD1\u4E00\u534A\u7684\u8DEF\u7A0B\u662F\u5728\u5317\u4EAC\u5E02\u533A\u7684\uFF0C\u8003\u8651\u5230\u5317\u4EAC\u7684\u96FE\u973E\u5929\u6C14\uFF0C\u5F3A\u70C8\u5EFA\u8BAE\u5728\u5E02\u533A\u9A91\u884C\u7684\u65F6\u5019\u5E26\u4E0A\u53E3\u7F69\u3002</p><p>\u884C\u7A0B\u653B\u7565\uFF1A</p><p>\u822A\u5929\u6865\uFF0D\u5B9A\u6167\u6865\uFF0D\u9996\u94A2\u96C6\u56E2\uFF0D\u6A21\u5F0F\u53E3\uFF0D\u4E09\u5BB6\u5E97\u6C34\u5E93\uFF0D\u519B\u5E84\uFF0D\u5999\u5CF0\u5C71\uFF0D\u519B\u5E84\uFF0D\u4E09\u5BB6\u5E97\u6C34\u5E93\uFF0D\u6A21\u5F0F\u53E3\uFF0D\u9996\u94A2\u96C6\u56E2\uFF0D\u5B9A\u6167\u6865\uFF0D\u822A\u5929\u6865 \u7EA6130\u516C\u91CC</p><p>\u8BE5\u9A91\u884C\u7EBF\u8DEF\uFF0C\u6700\u5927\u7684\u4EAE\u70B9\u662F\u4ECE\u5999\u5CF0\u5C71\u811A\u5230\u5C71\u9876\u768421\u516C\u91CC\u662F\u8FDE\u7EED\u76D8\u5C71\u516C\u8DEF\uFF0C7\u516C\u91CC\u7F13\u5761\uFF0C14\u516C\u91CC\u4E2D\u5761\u3002\u5728\u5355\u8F66\u9A91\u624B\u773C\u4E2D,\u8BE5\u6BB5\u722C\u5761\u8DEF\u7EBF\u6709\u7740\u72EC\u7279\u975E\u51E1\u65E0\u4E0E\u4F26\u6BD4\u7684\u9B45\u529B,\u5927\u6709\u4E0D\u722C\u5999\u5CF0\u5C71,\u6789\u4E3A\u5355\u8F66\u9A91\u624B\u7684\u611F\u89C9,\u4E0D\u8BBA\u662F\u516C\u8DEF\u8F66\u8FD8\u662F\u5C71\u5730\u8F66,\u53EA\u8981\u662F\u5355\u8F66\u9A91\u624B,\u8FD9\u4EAC\u897F\u5999\u5CF0\u5C71,\u52BF\u529B\u5728\u5176\u884C\u7A0B\u5B89\u6392\u4E4B\u5185</p><p>\u770B\u70B9\u63A8\u8350\uFF1A</p><p>\u5999\u5CF0\u5C71\u7EBF\u8DEF\u6CBF\u9014\u666F\u8272\u4E0D\u9519\uFF0C\u4ECE109\u56FD\u9053\u4E24\u65C1\u7684\u5C71\u8C37\uFF0C\u5230\u5999\u5CF0\u5C71\u5404\u4E2A\u666F\u70B9\uFF0C\u90FD\u975E\u5E38\u503C\u5F97\u4E00\u89C2\u3002\u73AB\u7470\u8C37\u82AC\u82B3\u5B9C\u4EBA\u3001\u6C38\u5B9A\u6CB3\u5E26\u6C34\u873F\u8712\u3001\u5982\u679C\u5929\u6C14\u6674\u597D\u7684\u8BDD\uFF0C\u4ECE\u5C71\u9876\u671B\u5411\u5317\u4EAC\u57CE\uFF0C\u9633\u53F0\u5C71\u3001\u671B\u4EAC\u697C\u5C42\u5C42\u53E0\u5D82\uFF0C\u8DB3\u4EE5\u5C55\u5F00\u773C\u5E18\u753B\u5377\u3002</p><p>tips:</p><p>\u722C\u5761\u9014\u4E2D\uFF0C14\u516C\u91CC\u7684\u4E2D\u5761\u5BF9\u9A91\u884C\u65B0\u624B\u6765\u8BF4\u662F\u4E00\u5927\u6311\u6218\u3002\u8981\u6CE8\u610F\u5408\u7406\u5B89\u6392\u722C\u5761\u901F\u5EA6\uFF0C\u4E3A\u907F\u514D\u819D\u76D6\u53D7\u4F24\uFF0C\u5EFA\u8BAE\u5C06\u53D8\u901F\u6863\u4F4D\u8C03\u4F4E\u3002</p><p>\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D</p><p>\u4E8C.\u767D\u7F8A\u6C9F\u7EBF</p><p>\u9A91\u884C\u533A\u57DF\u4ECB\u7ECD\uFF1A</p><p>\u767D\u7F8A\u6C9F\u81EA\u7136\u98CE\u666F\u533A\u4F4D\u4E8E\u660C\u5E73\u533A\u6D41\u6751\u9547\u897F\u5317\u90E8\uFF0C\u8DDD\u5FB7\u80DC\u95E840\u4F59\u516C\u91CC\u5904\uFF0C\u6C9F\u957F12\u516C\u91CC\uFF0C\u9762\u79EF3700\u516C\u9877\uFF0C\u666F\u533A\u5185\u67091958\u5E74\u4FEE\u5EFA\u7684\u5171\u9752\u56E2\u53F7\u6C34\u5E93\uFF0C\u6C34\u9762\u9762\u79EF700\u4F59\u4EA9\u3002\u8FC7\u4E86\u6C34\u5E93\uFF0C\u4FBF\u53EF\u770B\u5230\u81EA\u5C71\u4E2D\u98DE\u6D41\u800C\u51FA\u7684\u7011\u5E03\uFF0C\u6700\u5927\u65F640\u7C73\u843D\u5DEE\uFF0C\u867D\u4E0D\u89C1\u5176\u6C14\u52BF\u4E4B\u96C4\u4F1F\uFF0C\u5374\u53E6\u6709\u73E0\u5E18\u53E0\u7FE0\u4E4B\u611F\u3002\u666F\u533A\u5185\u4E00\u8DEF\u5947\u77F3\u5C0F\u6EAA\u7011\u5E03\u76F8\u4F34\uFF0C\u4E50\u8DA3\u65E0\u9650\u3002</p><p>\u767D\u7F8A\u6C9F\u5E76\u4E0D\u662F\u76DB\u540D\u5728\u5916\u7684\u666F\u70B9\uFF0C\u4E00\u662F\u56E0\u4E3A\u4EA4\u901A\u4E0D\u4FBF\uFF0C\u4E8C\u662F\u6EAA\u6C34\u533A\u9762\u79EF\u4E0D\u5927\uFF0C\u800C\u53E4\u8FF9\u53C8\u76F8\u8DDD\u8F83\u8FDC\uFF1B\u4F46\u662F\u4E8E\u521D\u7EA7\u9A91\u884C\u7231\u597D\u8005\u6765\u8BF4\u5374\u662F\u4E00\u6B21\u75DB\u5E76\u5FEB\u4E50\u7684\u4F53\u9A8C\u30022013\u5E74\u73AF\u5317\u4EAC\u56FD\u9645\u516C\u8DEF\u81EA\u884C\u8F66\u7ECF\u5178\u8D5B\u6BB5\u6D4B\u8BD5\u8D5B\u5728\u8BE5\u5730\u4E3E\u529E\uFF0C\u66F4\u52A0\u63D0\u9AD8\u4E86\u8BE5\u7EBF\u8DEF\u5728\u9A91\u884C\u7231\u597D\u8005\u4E2D\u7684\u77E5\u540D\u5EA6\u3002</p><p>\u9A91\u884C\u7EA7\u522B\uFF1A\u2605\u2605</p><p>\u4F53\u529B\u5F3A\u5EA6\uFF1A\u2605\u2605</p><p>\u6280\u672F\u96BE\u5EA6\uFF1A\u2605</p><p>\u60CA\u9669\u523A\u6FC0\uFF1A\u2605\u2605</p><p>\u81EA\u7136\u98CE\u5149\uFF1A\u2605\u2605\u2605</p><p>\u5386\u53F2\u6587\u5316\uFF1A\u2605\u2605</p><p>\u9A91\u884C\u8DEF\u7EBF\uFF1A</p><p>\u9A6C\u7538\uFF0D\u6C99\u6CB3\uFF0D\u9633\u574A\uFF0D\u6D41\u6751\uFF0D\u767D\u7F8A\u6C9F\uFF0D\u5766\u514B\u535A\u7269\u9986\uFF0D\u9633\u574A\uFF0D\u6C99\u6CB3\uFF0D\u9A6C\u7538</p><p>\u9A91\u884C\u5B63\u8282\uFF1A</p><p>\u9664\u96E8\u96EA\u5927\u98CE\u5929\u6C14\u4EE5\u5916\uFF0C\u5168\u5E74\u7686\u53EF\u3002\u672C\u7EBF\u8DEF\u4E0E\u5176\u4ED6\u5317\u4EAC\u8FD1\u90CA\u521D\u7EA7\u7EBF\u8DEF\u76F8\u6BD4\u6700\u5927\u7684\u4E0D\u540C\u5C31\u5728\u4E8E\u8DEF\u51B5\u8F83\u597D\uFF0C\u6CA1\u6709\u975E\u5E38\u56F0\u96BE\u7684\u9A91\u884C\u8DEF\u6BB5\uFF0C\u56E0\u6B64\u51AC\u5929\u4E5F\u80FD\u770B\u5230\u5F88\u591A\u9A91\u53CB\u524D\u5F80\u3002\u4F46\u63A8\u8350\u5728\u6625\u5929\u6216\u590F\u79CB\u4EA4\u63A5\u65F6\u6CBF\u9014\u98CE\u666F\u66F4\u597D</p><p>\u5168\u7EBF\u65F6\u95F4\uFF1A</p><p>\u5355\u65E5\u8FD4\u7A0B\uFF0C\u603B\u9A91\u884C\u65F6\u95F4\u7EA68\u5C0F\u65F6\u3002\u8BE5\u6761\u7EBF\u8DEF\u5728\u8FDB\u5165\u767D\u7F8A\u6C9F\u540E\u7684\u516D\u8FDE\u5F2F\u548C\u4E4B\u540E\u8FDE\u7EED\u768412\u516C\u91CC\u7F13\u5761\uFF0C\u867D\u96BE\u5EA6\u4E0D\u5927\uFF0C\u4F46\u4E5F\u8981\u9A91\u884C\u8005\u5408\u7406\u5206\u914D\u4F53\u529B\u3002</p><p>\u8DEF\u7EBF\u8BC4\u4EF7\uFF1A</p><p>\u767D\u7F8A\u6C9F\u5728\u5317\u4EAC\u9A91\u884C\u7231\u597D\u8005\u7684\u5708\u91CC\uFF0C\u4EAB\u6709\u7740\u76F8\u5F53\u9AD8\u7684\u77E5\u540D\u5EA6\uFF0C\u8FD9\u5728\u5F88\u5927\u7A0B\u5EA6\u4E0A\u662F\u56E0\u4E3A\u5B83\u8DDD\u79BB\u5317\u4EAC\u5E02\u533A\u7684\u8DDD\u79BB\u975E\u5E38\u9002\u5B9C\u9A91\u884C\u3002\u4ECE\u5317\u4EAC\u5E02\u533A\u9A6C\u7538\u6865\u51FA\u53D1\uFF0C\u5230\u767D\u7F8A\u6C9F\u53EA\u670950\u591A\u516C\u91CC\u7684\u8DEF\u7A0B\u3002</p><p>\u6574\u4E2A\u767D\u7F8A\u6C9F\u957F\u5EA6\u4E3A12\u516C\u91CC,\u5927\u90E8\u5206\u8DEF\u6BB5\u5747\u53EF\u9A91\u884C,\u5168\u7A0B\u7F13\u5761,\u6C9F\u4E2D\u8FD8\u53EF\u4EE5\u9493\u9C7C, \u6709\u95F2\u60C5\u9038\u81F4\u7684\u670B\u53CB\u53CB\u4E0D\u59A8\u5C06\u5782\u9493\u7684\u5BB6\u4F19\u4E00\u5E76\u5E26\u7740\u3002\u8FDB\u5165\u56DB\u6708\u4EFD\u8FD9\u91CC\u767E\u82B1\u76DB\u5F00, \u98CE\u666F\u5B9C\u4EBA,\u53C8\u6709\u6EAA\u6C34\u76F8\u4F34,\u53EF\u4EE5\u5C3D\u60C5\u5730\u4EAB\u53D7\u62E5\u62B1\u5927\u81EA\u7136\u7684\u4E50\u8DA3\u3002</p><p>\u767D\u7F8A\u6C9F\u8457\u540D\u7684\u201C\u9B54\u9B3C\u516D\u8FDE\u5F2F\u201D,\u5219\u662F\u6311\u6218\u9A91\u884C\u6280\u672F\u4E0E\u4F53\u80FD\u7684\u5730\u65B9,\u6709\u8FDE\u7EED\u4E09S\u5F62\u5F2F\u9053,\u597D\u5728\u8FD9\u91CC\u9053\u8DEF\u5E73\u5766,\u800C\u4E14\u6781\u5C11\u6709\u5927\u8F66\u7ECF\u8FC7,\u6240\u4EE5\u867D\u7136\u53F7\u79F0\u201C\u9B54\u9B3C\u201D, \u4E5F\u4E0D\u8FC7\u662F\u7ED9\u9A91\u884C\u589E\u52A0\u4E00\u4E9B\u60C5\u8DA3\u800C\u5DF2\u3002</p><p>\u5EFA\u8BAE\u4EBA\u6570\uFF1A</p><p>\u7531\u4E8E\u8BE5\u6761\u7EBF\u8DEF\u8FC7\u5F80\u8F66\u8F86\u4E0D\u591A\uFF0C\u539F\u5219\u4E0A\u6765\u8BF4\u5BF9\u4EBA\u6570\u8981\u6C42\u8F83\u4E3A\u5BBD\u677E\uFF0C\u4F46\u7531\u4E8E\u90E8\u5206\u9053\u8DEF\u8F83\u4E3A\u72ED\u7A84\uFF0C\u56E0\u6B64\u6700\u597D\u4E0D\u8981\u8D85\u8FC7\u5341\u4EBA\u3002</p><p>\u5FC5\u9700\u88C5\u5907\uFF1A</p><p>\u9632\u98CE\u4E0A\u8863\u3001\u6392\u6C57\u5185\u8863\u3001\u80CC\u5305\u3001\u58A8\u955C\u3001\u5934\u76D4\u3001\u624B\u5957</p><p>\u8BE5\u7EBF\u8DEF\u7531\u4E8E\u662F\u5317\u4EAC\u4EAC\u90CA\u7EBF\u8DEF\uFF0C\u56E0\u6B64\u6709\u5C06\u8FD1\u4E00\u534A\u7684\u8DEF\u7A0B\u662F\u5728\u5317\u4EAC\u5E02\u533A\u7684\uFF0C\u8003\u8651\u5230\u5317\u4EAC\u7684\u96FE\u973E\u5929\u6C14\uFF0C\u5EFA\u8BAE\u5728\u5E02\u533A\u9A91\u884C\u7684\u65F6\u5019\u5E26\u4E0A\u53E3\u7F69\u3002</p><p>\u884C\u7A0B\u653B\u7565\uFF1A</p><p>\u9A6C\u7538\uFF0D\u6E05\u6CB3\uFF0D\u6C99\u6CB3\uFF0D\u9633\u574A\u9547\uFF0D\u845B\u6751\uFF0D\u6D41\u6751\u9547\uFF0D\u767D\u7F8A\u57CE\uFF0D\u82A6\u5BB6\uFF0D\u7A91\u6E7E\uFF0D\u767D\u7F8A\u6C9F\u81EA\u7136\u98CE\u666F\u533A\uFF0D\u9AD8\u5D16\u53E3\uFF0D\u6D41\u6751\u9547\uFF0D\u9633\u574A\u9547\uFF0D\u6C99\u6CB3\uFF0D\u6E05\u6CB3\uFF0D\u9A6C\u7538 \u5168\u7A0B\u7EA6120\u516C\u91CC</p><p>\u81EA\u5317\u4EAC\u5E02\u533A\u51FA\u53D1\uFF0C\u6CBF\u516B\u8FBE\u5CAD\u9AD8\u901F\u7ECF\u9A6C\u7538\u3001\u6E05\u6CB3\u3001\u6C99\u6CB3\u9547\u5DE6\u8F6C\u5230\u9633\u574A\u5317\u845B\u6751\u897F\u884C\uFF0C\u5230\u6D41\u6751\u8FDB\u5C71\u95F4\u516C\u8DEF\uFF0C\u8DDF\u7740\u8DEF\u6807\u524D\u884C\u5373\u53EF\u62B5\u8FBE\u767D\u7F8A\u57CE\uFF0C\u8FD9\u91CC\u4FBF\u662F\u8FDB\u5165\u767D\u7F8A\u6C9F\u7684\u5173\u9698\u4E86\uFF0C\u8FC7\u4E86\u5171\u9752\u56E2\u6C34\u5E93\u540E\u4E0D\u4E45\u5C31\u662F\u90A3\u6BB5\u8457\u540D\u7684\u201C\u9B54\u9B3C\u516D\u8FDE\u5F2F\u201D\u3002</p><p>\u6CBF\u774012\u516C\u91CC\u7684\u767D\u7F8A\u6C9F\u5CE1\u8C37\u4E00\u8DEF\u800C\u4E0A\uFF0C\u8D70\u5B8C\u5168\u7A0B\u540E\uFF0C\u4F1A\u6709\u5927\u698210\u516C\u91CC\u5DE6\u53F3\u7684\u4E0B\u5761\u5C31\u5230\u4E86\u9AD8\u5D16\u53E3\uFF0C\u6CBF\u7740S\u5F62\u5F2F\u9053\u4E00\u76F4\u8D70\u5C31\u56DE\u5230\u4E86\u6D41\u6751\uFF0C\u63A5\u4E0B\u6765\u7684\u8DEF\u7A0B\u5C31\u8DDF\u6765\u65F6\u7684\u8DEF\u7A0B\u4E00\u81F4\u4E86\uFF0C\u53EF\u4EE5\u5728\u6765\u7A0B\u7565\u8FC7\u7684\u5766\u514B\u535A\u7269\u9986\u53C2\u89C2\u4E00\u756A\uFF0C\u518D\u8E0F\u4E0A\u56DE\u5BB6\u7684\u8DEF\u7A0B\u3002</p><p>\u770B\u70B9\u63A8\u8350\uFF1A</p><p>\u767D\u7F8A\u6C9F\u5CE1\u8C37\u957F\u5EA6\u4E3A12\u516C\u91CC\uFF0C\u5927\u90E8\u5206\u8DEF\u6BB5\u5747\u53EF\u9A91\u884C\uFF0C\u5761\u5EA6\u4E0D\u5927\uFF0C\u53C8\u6709\u6EAA\u6C34\u5C71\u82B1\u76F8\u4F34\uFF0C\u662F\u9A91\u884C\u7684\u7EDD\u4F73\u8DEF\u6BB5\uFF0C\u5927\u53EF\u5C3D\u60C5\u5730\u9A91\u7740\u7231\u8F66\u62E5\u62B1\u5927\u81EA\u7136\u3002</p><p>\u767D\u7F8A\u6C9F\u5F53\u5730\u5E76\u6CA1\u6709\u4EC0\u4E48\u7279\u522B\u7684\u7F8E\u98DF\uFF0C\u4F46\u5728\u8BE5\u7EBF\u8DEF\u7684\u9633\u574A\u9547\u7684\u9633\u574A\u80DC\u5229\u6DAE\u8089\u5374\u662F\u4E00\u7EDD\uFF0C\u662F\u5730\u9053\u7684\u8001\u5317\u4EAC\u6DAE\u8089\u3002\u4EE5\u201C\u8089\u8D28\u9C9C\u5AE9\u3001\u8C03\u6599\u72EC\u7279\u3001\u6C11\u65CF\u4F20\u7EDF\u3001\u7ECF\u6D4E\u5FEB\u6377\u201D\u8457\u79F0\uFF0C \u201C\u5317\u4EAC\u5E02\u8457\u540D\u5546\u6807\u201D\u3001\u201C\u4E2D\u56FD\u540D\u706B\u9505\u201D\u3001\u201C\u5341\u4F73\u4EAC\u57CE\u540D\u706B\u9505\u201D\u3001\u201C\u4E2D\u56FD\u9910\u996E\u7279\u8BB8\u4F53\u7CFB\u4F18\u79C0\u7BA1\u7406\u5956\u201D\u7B49\u8363\u8A89\u3002</p><p>tips:</p><p>\u8BE5\u7EBF\u8DEF\u7684\u9633\u574A\u524D\u8DEF\u6BB5\uFF0C\u5F80\u6765\u8F66\u8F86\u5C24\u5176\u662F\u5927\u8F66\u8F83\u591A\uFF0C\u8981\u6CE8\u610F\u9A91\u884C\u5B89\u5168\u3002</p><p>\u5BF9\u4E8E\u9A91\u884C\u65B0\u624B\u6216\u8005\u82E5\u611F\u89C9\u4ECE\u5E02\u533A\u5230\u5357\u53E3\u8FD9\u6BB5\u8DEF\u9014\u6BD4\u8F83\u4E4F\u5473\uFF0C\u8FD8\u53EF\u4EE5\u5728\u5FB7\u80DC\u95E8\u5E26\u8F66\u642D\u5927\u5DF4\u3002919\u8DEF\u5927\u5DF4\u90E8\u5206\u8F66\u5E26\u6709\u5DE8\u5927\u7684\u884C\u674E\u8231\uFF0C\u53EF\u4EE5\u5C06\u81EA\u884C\u8F66\u653E\u8FDB\u884C\u674E\u8231\u4E2D\uFF0C\u5F85\u62B5\u8FBE\u5357\u53E3\u9547\u65F6\uFF0C\u518D\u6B63\u5F0F\u5F00\u59CB\u9A91\u884C\uFF0C\u4E00\u4E0B\u4FBF\u7701\u53BB\u4E8630\u591A\u516C\u91CC\u7684\u52B3\u7D2F\u3002</p><p>\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D</p><p>\u4E09.\u73AF\u5341\u4E09\u9675\u6C34\u5E93\u7EBF</p><p>\u9A91\u884C\u533A\u57DF\u4ECB\u7ECD\uFF1A</p><p>\u5341\u4E09\u9675\u666F\u533A\u4F4D\u4E8E\u5317\u4EAC\u5317\u90E8\u7684\u660C\u5E73\u533A\u5883\u5185,\u8DDD\u5317\u4EAC\u5E02\u533A\u7EA640\u516C\u91CC.\u666F\u533A\u5185\u4E0D\u4EC5\u6709\u8457\u540D\u7684\u7684\u660E\u5341\u4E09\u9675,\u6CBF\u9014\u8FD8\u6709\u201C\u94C1\u81C2\u94F6\u5C71\u201D\u7B49\u81EA\u7136\u98CE\u8C8C,\u4E00\u8DEF\u9014\u5F84\u5341\u4E09\u9675\u6C34\u5E93,\u4E5D\u9F99\u6E38\u4E50\u56ED,\u83BD\u5C71\u56FD\u5BB6\u68EE\u6797\u516C\u56ED,\u5317\u4EAC\u56FD\u9645\u9AD8\u5C14\u592B\u7403\u573A\u7B49. \u6C34\u5E93\u5317\u5CB8\u5C31\u662F\u83BD\u5C71\u56FD\u5BB6\u68EE\u6797\u516C\u56ED,\u5C71\u4E0A\u6811\u6797\u8302\u5BC6,\u662F\u5317\u4EAC\u6700\u5927\u7684\u56FD\u5BB6\u68EE\u6797\u516C\u56ED\u4E4B\u4E00</p><p>\u8FD9\u6761\u9A91\u884C\u7EBF\u8DEF\u7531\u4E8E\u8DDD\u79BB\u5E02\u533A\u8F83\u8FD1\uFF0C\u6709\u5C71\u6709\u6C34\u6709\u98CE\u666F\uFF0C\u8DEF\u51B5\u8F83\u597D\uFF0C\u56E0\u6B64\u5BF9\u4E8E\u9A91\u884C\u7231\u597D\u8005\u5C24\u5176\u662F\u9A91\u884C\u65B0\u624B\u6765\u8BF4\uFF0C\u662F\u4E0D\u53EF\u591A\u5F97\u7684\u4E00\u6761\u9EC4\u91D1\u7EBF\u8DEF\u3002</p><p>\u9A91\u884C\u7EA7\u522B\uFF1A\u2605\u2605</p><p>\u4F53\u529B\u5F3A\u5EA6\uFF1A\u2605\u2605</p><p>\u6280\u672F\u96BE\u5EA6\uFF1A\u2605</p><p>\u60CA\u9669\u523A\u6FC0\uFF1A\u2605\u2605</p><p>\u81EA\u7136\u98CE\u5149\uFF1A\u2605\u2605\u2605</p><p>\u5386\u53F2\u6587\u5316\uFF1A\u2605\u2605\u2605</p><p>\u9A91\u884C\u8DEF\u7EBF\uFF1A</p><p>\u5065\u7FD4\u6865\uFF0D\u9633\u574A\uFF0D\u5357\u53E3\uFF0D\u660C\u5E73\uFF0D\u5341\u4E09\u9675\u6C34\u5E93\uFF0D\u87D2\u5C71\uFF08\u8DEF\u7EBF\u56FE\u4E2D\u4E3A\u5355\u7A0B\uFF09</p><p>\u9A91\u884C\u5B63\u8282\uFF1A</p><p>3\u201411\u6708\u3002 \u8FD9\u6761\u8DEF\u7EBF\u9664\u4E86\u51AC\u5B63\u4E0B\u96EA\u548C\u6625\u79CB\u5B63\u98CE\u529B\u8F83\u5927\u7684\u4E2A\u522B\u5929\u6C14\u5916\u90FD\u6BD4\u8F83\u9002\u5B9C.\u4F46\u63A8\u8350\u5728\u6625\u5929\u5C71\u82B1\u70C2\u6F2B\u6216\u590F\u79CB\u4EA4\u63A5\u7684\u91C7\u6458\u5B63\u8282\u4E3A\u597D</p><p>\u5168\u7EBF\u65F6\u95F4\uFF1A</p><p>\u5355\u65E5\u8FD4\u7A0B\uFF0C\u9A91\u884C\u65F6\u95F46\u5C0F\u65F6\u3002\u7531\u4E8E\u662F\u73AF\u6C34\u5E93\u9A91\u884C\uFF0C\u4E14\u5468\u56F4\u666F\u533A\u8F83\u591A\uFF0C\u5EFA\u8BAE\u4E0D\u8981\u7740\u6025\u8D76\u8DEF\uFF0C\u800C\u662F\u9002\u5F53\u653E\u7F13\u65F6\u95F4\uFF0C\u4EAB\u53D7\u6CBF\u9014\u666F\u8272\u3002</p><p>\u8DEF\u7EBF\u8BC4\u4EF7\uFF1A</p><p>\u5341\u4E09\u9675\u6C34\u5E93\u662F\u9A91\u884C\u7231\u597D\u8005\u7ECF\u5E38\u8D70\u7684\u8DEF\u7EBF\uFF0C\u73AF\u5F62\u8DEF\u7A0B\u4E3A18\u516C\u91CC\uFF0C\u6B64\u8DEF\u7EBF\u5927\u90E8\u5206\u662F\u5E73\u5766\u7684\u8DEF\u9762,\u8FD0\u52A8\u5F3A\u5EA6\u5C0F,\u8DEF\u7EBF\u96BE\u5EA6\u4F4E,\u662F\u4E00\u6761\u9002\u5408\u5468\u672B\u653E\u677E\u7684\u8DEF\u7EBF\u3002\u4E0D\u8FC7\u7531\u4E8E\u516B\u8FBE\u5CAD\u9AD8\u901F\u8DEF\u662F\u4E3B\u8981\u5E72\u9053\uFF0C\u6240\u4EE5\u8DEF\u4E0A\u8F66\u8F86\u8F83\u591A\uFF0C\u9700\u8981\u6CE8\u610F\u9A91\u884C\u5B89\u5168\uFF0C\u8FDB\u5165\u5341\u4E09\u9675\u73AF\u6C34\u5E93\u9A91\u884C\u65F6\uFF0C\u8F66\u8F86\u5C31\u76F8\u5BF9\u5C11\u4E86\u5F88\u591A\uFF0C\u4F46\u7531\u4E8E\u5C71\u8DEF\u8D77\u4F0F\u8F83\u5927\uFF0C\u800C\u4E14\u591A\u5F2F\uFF0C\u6240\u4EE5\u8981\u63A7\u5236\u597D\u901F\u5EA6\uFF0C\u907F\u514D\u610F\u5916\u53D1\u751F\u3002\u503C\u5F97\u4E00\u8BF4\u7684\u662F\u4ECE\u6C34\u575D\u4E1C\u4FA7\u7ECF\u5927\u575D\u5317\u4FA7\u7684\u4E00\u6BB5\u4E0A\u5761\u8DEF\uFF0C\u5761\u9661\u8DEF\u957F\uFF0C\u5927\u6982\u6709\u5C06\u8FD1500\u7C73</p><p>\u5FC5\u9700\u88C5\u5907\uFF1A</p><p>\u7531\u4E8E\u8981\u7ED5\u884C\u5341\u4E09\u9675\u6C34\u5E93\u4E00\u5468\uFF0C\u8DEF\u51B5\u8F66\u4E0D\u662F\u7279\u522B\u597D\uFF0C\u56E0\u6B64\u6700\u597D\u662F\u516C\u8DEF\u8D5B\u6216\u5C71\u5730\u8F66\u3002</p><p>\u9632\u98CE\u4E0A\u8863\u3001\u6392\u6C57\u5185\u8863\u3001\u80CC\u5305\u58A8\u955C\u3001\u5934\u76D4\u3001\u624B\u5957\u5FC5\u987B\u5E26\u4E0A</p><p>\u8BE5\u7EBF\u8DEF\u7684\u524D\u534A\u7A0B\u8DEF\u9762\u626C\u5C18\u8F83\u591A\uFF0C\u82E5\u662F\u5728\u522B\u7684\u57CE\u5E02\uFF0C\u4E00\u5757\u9B54\u672F\u5DFE\u5C31\u53EF\u4EE5\u89E3\u51B3\u95EE\u9898\u3002\u4F46\u518D\u8003\u8651\u5230\u5317\u4EAC\u7684\u96FE\u973E\u5929\u6C14\uFF0C\u56E0\u6B64\u5F3A\u70C8\u5EFA\u8BAE\u5728\u5E02\u533A\u9A91\u884C\u7684\u65F6\u5019\u5E26\u4E0A\u53E3\u7F69\u3002</p><p>\u884C\u7A0B\u653B\u7565\uFF1A</p><p>\u9890\u548C\u56ED\uFF0D\u4E0A\u6E05\u6865\uFF0D\u6C99\u6CB3\uFF0D\u9633\u574A\uFF0D\u5357\u53E3\uFF0D\u660C\u5E73\uFF0D\u5341\u4E09\u9675\u6C34\u5E93\uFF0D\u6C34\u5E93\u73AF\u7EBF\uFF0D\u87D2\u5C71\uFF0D\u660C\u5E73\uFF0D\u5357\u53E3\uFF0D\u9633\u574A\uFF0D\u6C99\u6CB3\uFF0D\u4E0A\u6E05\u6865\uFF0D\u9890\u548C\u56ED \u7EA6110\u516C\u91CC</p><p>\u5341\u4E09\u9675\u6C34\u5E93\u662F\u9A91\u884C\u7231\u597D\u8005\u7ECF\u5E38\u8D70\u7684\u8DEF\u7EBF\uFF0C\u7531\u4E8E\u516B\u8FBE\u5CAD\u9AD8\u901F\u8DEF\u662F\u4E3B\u8981\u5E72\u9053\uFF0C\u6240\u4EE5\u8DEF\u4E0A\u8F66\u8F86\u8F83\u591A\uFF0C\u9700\u8981\u6CE8\u610F\u9A91\u884C\u5B89\u5168\uFF0C\u8FDB\u5165\u5341\u4E09\u9675\u73AF\u6C34\u5E93\u9A91\u884C\u65F6\uFF0C\u8F66\u8F86\u5C31\u76F8\u5BF9\u5C11\u4E86\u5F88\u591A\uFF0C\u4F46\u7531\u4E8E\u5C71\u8DEF\u8D77\u4F0F\u8F83\u5927\uFF0C\u800C\u4E14\u591A\u5F2F\uFF0C\u6240\u4EE5\u8981\u63A7\u5236\u597D\u901F\u5EA6\uFF0C\u907F\u514D\u610F\u5916\u53D1\u751F\u3002</p><p>\u5728\u6C34\u5E93\u73AF\u7EBF\uFF0C\u62AC\u773C\u671B\u53BB\uFF0C\u53EF\u4E00\u770B\u5230\u7FE0\u67CF\u8471\u90C1,\u9C9C\u82B1\u904D\u5730,\u83BD\u5C71\u7EF5\u5EF6\u800C\u6765\u3002\u73AF\u7ED5\u6C34\u5E93\u7684\u5C71\u91CC,\u975E\u5E38\u5B89\u9759,\u8DEF\u51B5\u8F83\u597D\u5730\u6BB5\u53EF\u4EE5\u7A0D\u5FAE\u5FEB\u901F\u9A91\u884C,\u5C3D\u60C5\u4EAB\u53D7\u9A91\u884C\u7684\u4E50\u8DA3\u3002</p><p>\u770B\u70B9\u63A8\u8350\uFF1A</p><p>\u5C31\u5317\u4EAC\u8FD1\u90CA\u800C\u8A00\uFF0C\u5341\u4E09\u9675\u6C34\u5E93\u73AF\u7EBF\u662F\u76F8\u5BF9\u6765\u8BF4\u4E00\u5929\u65F6\u95F4\u53EF\u4EE5\u9A91\u5B8C\uFF0C\u5E76\u4E14\u6709\u5145\u5206\u65F6\u95F4\u53EF\u4EE5\u611F\u53D7\u5C71\u4E0E\u6C34\u4E50\u8DA3\u7684\u4E00\u6761\u7EBF\u8DEF\u3002\u6CBF\u7EBF\u666F\u533A\u5982\u5341\u4E09\u9675\u3001\u5341\u4E09\u9675\u6C34\u5E93\u3001\u83BD\u5C71\u7B49\u666F\u70B9\u548C\u914D\u5957\u8BBE\u65BD\u90FD\u5DF2\u7ECF\u5F00\u53D1\u7684\u8F83\u4E3A\u5B8C\u5584\uFF0C\u4E00\u8DEF\u4E0A\u53EF\u4EE5\u5145\u5206\u4EAB\u53D7\u8FB9\u9A91\u884C\u8FB9\u770B\u7F8E\u666F\u7684\u611F\u89C9\u3002</p><p>\u5341\u4E09\u9675\u7684\u7076\u53F0\u6C34\u5E93\u9C7C\u975E\u5E38\u6709\u540D\uFF0C\u4F89\u7096\u9C7C\u90FD\u5F88\u5927\uFF0C\u90FD\u670910\u65A4\u5DE6\u53F3\uFF0C\u63A8\u8350\u5C1D\u8BD5\u3002</p><p>\u666F\u533A\u9644\u8FD1\u8FD8\u6709\u4E00\u4E9B\u8349\u8393\u91C7\u6458\u56ED\uFF0C\u8D76\u4E0A\u4E86\u5B63\u8282\u8FD8\u53EF\u4EE5\u53BB\u611F\u53D7\u4E00\u4E0B\u91C7\u6458\u7684\u4E50\u8DA3\u3002</p><p>tips:</p><p>\u4ECE\u660C\u5E73\u5230\u5341\u4E09\u9675\u6C34\u5E93\u5927\u575D\u4E3A\u4E0A\u4E0B\u8D77\u4F0F\u8DEF\uFF0C\u87D2\u5C71\u811A\u4E0B\u5230\u5C71\u9876\u7EA69\u516C\u91CC\u5C71\u8DEF\uFF0C\u4E0B\u5C71\u65F6\u6CE8\u610F\u5176\u98A0\u7C38\u8DEF\u6BB5\uFF0C\u66F4\u8981\u6CE8\u610F\u4E0D\u8981\u89E6\u78B0\u4E2D\u592E\u9694\u79BB\u4FE1\u53F7\u88C5\u7F6E\uFF0C\u5426\u5219\u5F88\u5BB9\u6613\u53D1\u751F\u5371\u9669\u3002</p><p>\u8FD4\u7A0B\u4E2D,\u6E05\u6CB3,\u6C99\u6CB3\u6BB5\u8D27\u8F66\u8F83\u591A,\u901A\u8FC7\u65F6\u6CE8\u610F\u89C2\u5BDF\u6765\u5F80\u8F66\u8F86\u3002</p><p>\u6CE8\u610F\u4F53\u529B\u5206\u914D\uFF0C\u5C3D\u91CF\u5300\u901F\u524D\u8FDB\uFF0C\u8010\u529B\u662F\u6700\u91CD\u8981\u7684\uFF0C\u901F\u5EA6\u5176\u6B21\u3002</p><p>\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D\uFF0D</p><p>\u56DB.\u5341\u6E21\u7EBF</p><p>\u9A91\u884C\u533A\u57DF\u4ECB\u7ECD\uFF1A</p><p>\u5341\u6E21\u98CE\u666F\u533A\u4F4D\u4E8E\u623F\u5C71\u533A\u5883\u5185\u7684\u62D2\u9A6C\u6CB3\u4E2D\u4E0A\u6E38\uFF0C\u8DDD\u4EAC\u57CE\u533A100\u4F59\u516C\u91CC\uFF0C\u56E0\u4ECE\u5F20\u574A\u8FDB\u5165\u5341\u6E21\u9700\u7ECF\u62D2\u9A6C\u6CB3\u5341\u4E2A\u6E21\u53E3\u800C\u5F97\u540D\u3002\u8FD9\u7247\u5730\u533A\u4EE5\u72EC\u7279\u7684\u5580\u65AF\u7279\u5730\u8C8C\u79F0\u7EDD\uFF0C\u662F\u534E\u5317\u5730\u533A\u6700\u5927\u7684\u5CA9\u6EB6\u5927\u5CE1\u8C37\u3002</p><p>\u4ECE\u5317\u4EAC\u5E02\u533A\u9A91\u884C\u5341\u6E21\u8FD9\u6761\u7EBF\u8DEF\uFF0C\u662F\u5317\u4EAC\u5355\u8F66\u7231\u597D\u8005\u7ECF\u5178\u7684\u9A91\u884C\u7EBF\u8DEF\u4E4B\u4E00\u3002\u6CBF\u9014\u4E0D\u4EC5\u98CE\u666F\u79C0\u4E3D\uFF0C\u8FD8\u6709\u5468\u53E3\u5E97\u53E4\u733F\u4EBA\u9057\u5740\u3001\u59CB\u5EFA\u4E8E\u968B\u672B\u7684\u4E91\u5C45\u5BFA\u4EE5\u53CA\u53E4\u8001\u7684\u5341\u6E21\u81EA\u7136\u666F\u89C2\u3002\u9A91\u884C\u5176\u4E2D\uFF0C\u53E4\u8001\u7684\u6C14\u606F\u6251\u9762\u800C\u6765\uFF0C\u8BA9\u4EBA\u5728\u60CA\u8BE7\u5927\u81EA\u7136\u7684\u9B3C\u65A7\u795E\u5DE5\u4E4B\u4F59\uFF0C\u4E5F\u4E0D\u7981\u6168\u53F9\u5C81\u6708\u7684\u6CA7\u6851\u3002</p><p>\u9A91\u884C\u7EA7\u522B\uFF1A\u2605\u2605</p><p>\u4F53\u529B\u5F3A\u5EA6\uFF1A\u2605\u2605\u2605</p><p>\u6280\u672F\u96BE\u5EA6\uFF1A\u2605\u2605</p><p>\u60CA\u9669\u523A\u6FC0\uFF1A\u2605\u2605\u2605</p><p>\u81EA\u7136\u98CE\u5149\uFF1A\u2605\u2605\u2605\u2605</p><p>\u5386\u53F2\u6587\u5316\uFF1A\u2605\u2605\u2605</p><p>\u9A91\u884C\u8DEF\u7EBF\uFF1A</p><p>\u516C\u4E3B\u575F\uFF0D\u5362\u6C9F\u6865\uFF0D\u826F\u4E61\uFF0D\u5F20\u574A\uFF0D\u5341\u6E21\uFF0D\u5F20\u574A\uFF0D\u826F\u4E61\uFF0D\u5362\u6C9F\u6865\uFF0D\u516C\u4E3B\u575F</p><p>\u9A91\u884C\u5B63\u8282\uFF1A</p><p>\u4E00\u5E74</p>",126),o=[a];function c(r,n,i,d,m,l){return e(),t("div",null,o)}const T=p(s,[["render",c]]);export{S as __pageData,T as default};
